import './App.css';
import React,{useState} from 'react';


const today=()=>{
  //return new Date().toISOString();
  const now = new Date();
  const options = { timeZone:'Asia/Seoul', hour12:true };
  return now.toLocaleString('ko-KR', options);
} 

function Time(props){
  return(
    <div>
      <h2>What's DateTime?</h2>
      <p>{props.today}</p>
    </div>
  )
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Time today={today()} />
      </header>
    </div>
  );
}

export default App;
