import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';


const Container = styled.div`
  text-align:center;
`;
const Button = styled.button`
  padding:10px 20px;
  font-size:18px;
  cursor:pointer;
  background-color:#ccc;
  border:none;
  border-radius:7px;
  &:hover{
    background-color:#e69;
  }
`;

const LoadingSpinner = styled.div`
  border:4px solid #eee;
  border-top:4px solid #39b;
  border-radius:50%;
  width:60px; 
  height:60px;
  margin:0 auto;
  animation:spin 2.5s linear infinite;
  @keyframes spin{
    0%{ transform:rotate(0deg)}
    100%{ transform:rotate(360deg)}
  }
`;

const cityMapping = {
  '서울':'Seoul',
  '인천':'Incheon',
  '수원':'Suwon-si',
  '성남':'Seongnam-si',
  '고양':'Goyang-si',
  '부산':'Busan',
  '대구':'Daegu',
  '광주':'Gwangju',
  '대전':'Daejeon',
  '울산':'Ulsan',
  '세종':'Sejong'
}

function WeatherPage(){
  const navigate = useNavigate();
  const { region, city } = useParams();
  const [weatherData, setWeatherData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [forecastData, setForecastData] = useState(null);

  useEffect(()=>{
    const fetchWeather = async () =>{
      setIsLoading(true);
      const apiKey = '28e07dfa61cced0cd42f9dc297ccc2f8'; //나의 apiKey
      const mappedCity = cityMapping[city] || city;
      try{
        const weatherResponse = await axios.get(`https://api.openweathermap.org/data/2.5/weather`, {
          params:{
            q:`${mappedCity},KR`,
            appid:apiKey,
            units:'metric',
            lang:'kr',
          }
        });
        const forecastResponse = await axios.get(`https://api.openweathermap.org/data/2.5/forecast`, {
          params:{
            q:`${mappedCity},KR`,
            appid:apiKey,
            units:'metric',
            lang:'kr',
          }
        });
        setWeatherData(weatherResponse.data);
        setForecastData(forecastResponse.data);
      } catch (error) {
        alert(error.response.data.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchWeather();
  }, [city])

  const backMove = ()=>{
    navigate('/');
  }
  




  return(
    <Container>
      <h1>{`${city}`}</h1>
      {weatherData && forecastData ? (
        <div>
          <div className="forecast">
            <div className='currentTemp'>
              <h2>
                <img src={`https://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`} alt={weatherData.weather[0].description} 
                style={{ width: '100px', height: '100px' }} />
              </h2>
              <p>{Math.round(weatherData.main.temp)}&deg; </p>
            </div>
            <div className='temp'>
              <p>최저 : {Math.round(weatherData.main.temp_min)}&deg;</p>
              <p>최고 : {Math.round(weatherData.main.temp_max)}&deg;</p>
            </div>
            <h2>오늘</h2>
            <div className="now">
            {
              forecastData.list
              .filter((forecast) => forecast.dt * 1000 >= Date.now()) // 현재 시간 이후의 데이터만 필터링
              .map((forecast, index) => (
                <div key={index} className="forecast">
                  <p><strong>{new Date(forecast.dt * 1000).toLocaleTimeString([], { hour: '1-digit' })}</strong></p>
                  <h2>
                    <img src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`} alt={weatherData.weather[0].description} 
                    style={{ width: '100px', height: '100px' }} />
                  </h2>
                  <p>온도 : {Math.round(forecast.main.temp)}&deg;C </p>
                </div>
              ))
            }
            </div>
            <h2>이번 주 예보</h2>
          </div>
          {
            forecastData.list.filter((_,index)=>index%8===0).map((forecast,index)=>(
              <div key={index} className="forecast">
                <p><strong>{new Date().toLocaleDateString(undefined, { month: '2-digit', day: '2-digit' })}</strong></p>
                <h2>{forecast.weather[0].description}
                <img src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`} alt={weatherData.weather[0].description} 
              style={{ width: '100px', height: '100px' }} /></h2>
                <p>온도 : {forecast.main.temp}&deg;C </p>
                <p>체감온도 : {forecast.main.feels_like}°C </p>
                <p>풍속 : {forecast.wind.speed} m/s</p>
              </div>
            ))
          }
        </div>
      ) : (
        <div>
          <LoadingSpinner />
          <p>날씨 정보를 불러오는 중입니다...</p>
        </div>
      )}
      <Button onClick={backMove}>다른지역 선택</Button>
    </Container>
  )
}

export default WeatherPage;
