import React, { useState } from 'react';

const seats = Array.from({ length: 20 }, (_, index) => ({
  id: index + 1,
  booked: false,
}));

const SeatSelection = () => {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [personCount, setPersonCount] = useState(1);
  const ticketPrice = 10000; // 예시 가격

  const toggleSeat = (id) => {
    if (selectedSeats.includes(id)) {
      setSelectedSeats(selectedSeats.filter(seat => seat !== id));
    } else if (selectedSeats.length < personCount) {
      setSelectedSeats([...selectedSeats, id]);
    }
  };

  const totalPrice = personCount * ticketPrice;

  return (
    <div>
      <h1>영화 예매</h1>
      <div>
        <label>
          인원 수:
          <input
            type="number"
            value={personCount}
            onChange={(e) => setPersonCount(Math.max(1, e.target.value))}
            min="1"
          />
        </label>
      </div>
      <h2>좌석 선택</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', width: '200px' }}>
        {seats.map(seat => (
          <div
            key={seat.id}
            onClick={() => toggleSeat(seat.id)}
            style={{
              width: '30px',
              height: '30px',
              margin: '5px',
              backgroundColor: selectedSeats.includes(seat.id) ? 'green' : 'lightgray',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              cursor: 'pointer'
            }}
          >
            {seat.id}
          </div>
        ))}
      </div>
      <h2>가격 종합</h2>
      <p>선택한 좌석 수: {selectedSeats.length}</p>
      <p>총 가격: {totalPrice} 원</p>
    </div>
  );
};

export default SeatSelection;
