import './App.css';
import React,{useState} from 'react';

function App() { /* const App=()=>{ */
  let [memo, setMemo] = useState([]);
  let [inputValue, setInputValue] = useState('');
  const addMemo=()=>{
    setMemo([...memo, inputValue]); // 배열에 입력값 추가
    setInputValue(''); // 입력필드 공간 초기화
  }


  const removeMemo =(index)=>{
    const newMemo = memo.filter((_,i)=> i!==index);
    setMemo(newMemo);
  }  
  return (
    <div className="App">
      <h1> Memo List </h1>
      <input type="text" value={inputValue}
            onChange={(e)=>{ setInputValue(e.target.value); } }
            placeholder="메모하세요" />
      <button type="button" onClick={addMemo}>추가</button>
      <ul>
        { memo.map((text,index)=>(
          <li key={index}>
            {text} : 
            <button onClick={()=>{ removeMemo(index) }}>Delete</button>            
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
