import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Swiper, SwiperSlide } from 'swiper/react';
import { EffectFade, Autoplay } from 'swiper/modules';

import 'swiper/css';
import 'swiper/css/effect-fade';
{/*
const TopBannerWrapper = styled.div`
  width:100%;
  height:50px;
  position:relative;
  overflow:hidden;
`;
const TopBannerItem = styled.div`
  position:absolute;
  top:0;
  left:50%;
  width:1920px;
  height:50px;
  margin-left:-960px;
`;
    <TopBannerWrapper>
      {
        banners.map((banner,idx)=>(
          <TopBannerItem key={idx}>
            <img src={banner.image} alt={banner.alt} />
          </TopBannerItem>
        ))
      }
    </TopBannerWrapper> 
*/}

function Topbanner(){
  const banners = [
    {image:`${process.env.PUBLIC_URL}/images/topbanner1.jpg`, alt:'Top Banner1'},
    {image:`${process.env.PUBLIC_URL}/images/topbanner2.jpg`, alt:'Top Banner2'},
    {image:`${process.env.PUBLIC_URL}/images/topbanner5.jpg`, alt:'Top Banner5'},
  ];
  return(    
    <div className="top-banner">
      <Swiper
        modules={[EffectFade, Autoplay]}
        effect="fade"
        fadeEffect={{ crossFade:true }}
        autoplay={{delay:3000, disableOnInteraction:false }}
        loop={true}
        className="mySwiper"
      >
        {
          banners.map((banner,idx)=>(
            <SwiperSlide key={idx}>
              <img src={banner.image} alt={banner.alt} className="topbanner-img" />
            </SwiperSlide>
          ))
        }
      </Swiper>
    </div>
  )
}

export default Topbanner;
