import React, { useState } from 'react';

const cityMapping = {
  '서울특별시': {
    '강남구': ['삼성동', '대치동', '청담동'],
    '서초구': ['서초동', '방배동', '잠원동'],
    '강북구': ['수유동', '미아동']
  },
  '인천광역시': {
    '미추홀구': ['숭의동', '용현동'],
    '남동구': ['구월동', '간석동']
  },
  '경기도 수원시': {
    '팔달구': ['매산동', '우만동'],
    '영통구': ['영통동', '망포동']
  },
  // 더 많은 도시, 구, 동 정보 추가 가능
};

function CitySelector({ onCitySelect, onInputClick }) {
  const [selectedCity, setSelectedCity] = useState('');  // 선택된 시
  const [selectedDistrict, setSelectedDistrict] = useState('');  // 선택된 구
  const [selectedTown, setSelectedTown] = useState('');  // 선택된 동

  const [filteredCities, setFilteredCities] = useState([]); // 검색된 시
  const [filteredDistricts, setFilteredDistricts] = useState([]); // 선택된 시의 구 목록
  const [filteredTowns, setFilteredTowns] = useState([]); // 선택된 구의 동 목록

  // 시 검색
  const handleCitySearchChange = (event) => {
    const value = event.target.value;
    if (value) {
      const filtered = Object.keys(cityMapping).filter(city =>
        city.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredCities(filtered);
    } else {
      setFilteredCities([]);
    }
  };

  // 시 선택
  const selectCity = (city) => {
    setSelectedCity(city);
    setFilteredDistricts(Object.keys(cityMapping[city]));  // 해당 시의 구 목록 설정
    setFilteredCities([]);
    setSelectedDistrict('');  // 구 초기화
    setSelectedTown('');  // 동 초기화
    setFilteredTowns([]);  // 동 목록 초기화
  };

  // 구 선택
  const selectDistrict = (district) => {
    setSelectedDistrict(district);
    setFilteredTowns(cityMapping[selectedCity][district]);  // 해당 구의 동 목록 설정
    setFilteredDistricts([]);
    setSelectedTown('');  // 동 초기화
  };

  // 동 선택
  const selectTown = (town) => {
    setSelectedTown(town);
    setFilteredTowns([]);
    onCitySelect(`${selectedCity} ${selectedDistrict} ${town}`); // 최종 선택된 시/구/동 전달
  };

  return (
    <div className="search">
      {/* 시 검색 input */}
      {!selectedCity && (
        <>
          <input
            type="text"
            placeholder="시 검색"
            onChange={handleCitySearchChange}
            onClick={onInputClick}
          />
          {filteredCities.length > 0 && (
            <ul>
              {filteredCities.map((city, index) => (
                <li key={index} onClick={() => selectCity(city)}>
                  {city}
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {/* 구 선택 */}
      {selectedCity && !selectedDistrict && (
        <>
          <p>선택된 시: {selectedCity}</p>
          <ul>
            {filteredDistricts.map((district, index) => (
              <li key={index} onClick={() => selectDistrict(district)}>
                {district}
              </li>
            ))}
          </ul>
        </>
      )}

      {/* 동 선택 */}
      {selectedDistrict && (
        <>
          <p>선택된 구: {selectedDistrict}</p>
          <ul>
            {filteredTowns.map((town, index) => (
              <li key={index} onClick={() => selectTown(town)}>
                {town}
              </li>
            ))}
          </ul>
        </>
      )}

      {/* 최종 선택 결과 */}
      {selectedCity && selectedDistrict && selectedTown && (
        <p>선택된 지역: {selectedCity} {selectedDistrict} {selectedTown}</p>
      )}
    </div>
  );
}

export default CitySelector;
