import React,{useState, useEffect, useCallback, useMemo} from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Thumbs } from 'swiper/modules';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart } from '../features/cartSlice';
import { validateCartItem, calculateItemTotal } from '../utils/cartUtils';
import { useSpring, animated, useTransition } from 'react-spring';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/thumbs';

function ProductDetail(){
  const {id} = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const location = useLocation();

  const [thumbsSwiper, setThumbsSwiper] = useState(null);// swiper썸네일 제어
  const [selectedColor, setSelectedColor] = useState(''); // 색상 설정
  const [selectedSize, setSelectedSize] = useState(''); // 크기 설정
  const [quantity, setQuantity] = useState(1); // 수량 설정
  const [ product, setProduct] = useState(null); //표시중 상품데이터 설정
  const [error, setError] = useState(''); // 오류 메세지 정의

  useEffect(()=>{
    if (location.state && location.state.product) {
      setProduct(location.state.product);
    } else {
      console.log("제품 데이터를 가져오는 로직 구현 필요");
    }
  },[location, id])
  /*
  const productFromStore = useSelector( state => {
    const products = state.product?.products || [];
    //state객체에 product속성이 null이면 [] 배열이 할당, .product속성값 할당
    return products.find(p=>p.id===id);//p(각제품객체)의 id와 외부id와 같은 제품객체 반환
  })
  */
  const handleAddCart = useCallback(()=>{
    if( !product ) return;
    const cartItem = {
      id:id,
      name:product.name,
      price:product.discountPrice || product.price,
      quantity:quantity,
      selectedColor:selectedColor,
      selectedSize:selectedSize,
      image:product.images[0]
    }
    try{
      validateCartItem(cartItem); //cartItem유효성 검사
      dispatch(addToCart(cartItem)); //redux스토어에 장바구니 추가 액션 디스패치
      setError('');
      alert('제품정보를 장바구니에 전달성공');
      navigate('/cart');
    } catch (err) { //cartItem유효성 검사에서 오류 발생시
      console.error('유효성 검사에러',err.message);
      setError(err.message);
    }
  },[dispatch, product, id, selectedColor, selectedSize, quantity, navigate]);

  const handleColorChange = useCallback((color)=>{
    setSelectedColor(color);
  },[]);

  const handleSizeChange = useCallback((size)=>{
    setSelectedSize(size);
  },[]);

  const handleQuantityChange = useCallback((change)=>{
    setQuantity(prevQuantity=>Math.max(1, prevQuantity+change))
  },[]);

  const totalPrice = useMemo(() => 
    product ? calculateItemTotal({ ...product, quantity }) : 0,
    [product, quantity]
  );
  /*
  useEffect(()=>{
    const mockProducts = {
      'collection-1': {
        name: '컬렉션 아이템 1',
        price: 49000,
        discountPrice: 39000,
        colors: ['BLACK', 'NAVY', 'GRAY'],
        sizes: ['XS', 'S', 'M', 'L'],
        images: [
          '/images/exhibition_bn01.jpg',
          '/images/exhibition_bn01.jpg',
          '/images/exhibition_bn01.jpg',
        ],
      },
      'special-1': {
        name: '멀티플라이 탑 민트아일랜드',
        price: 43000,
        discountPrice: 43000,
        colors: ['MINT', 'WHITE'],
        sizes: ['S', 'M', 'L'],
        images: [
          '/images/0170010020123.jpg',
          '/images/0170010020123.jpg',
          '/images/0170010020123.jpg',
        ],
      },
      'best-all-1': {
        name: '위드 잇 백 블랙',
        price: 39000,
        discountPrice: 39000,
        colors: ['BLACK'],
        sizes: ['ONE SIZE'],
        images: [
          '/images/0170010020353.jpg',
          '/images/0170010020353.jpg',
          '/images/0170010020353.jpg',
        ],
      },
    }

    setProduct(mockProducts[id] || productFromStore || null);
  },[id, productFromStore]);
  */
  if(!product){
    return <div>제품을 찾을수가 없습니다.</div>
  }

  const renderColorButtons = product.colors.map(color => (
    <button
      key={color}
      onClick={() => handleColorChange(color)}
      className={selectedColor === color ? 'selected' : ''}
    >
      {color}
    </button>
  ));
    
  const renderSizeButtons = product.sizes.map(size => (
    <button
      key={size}
      onClick={() => handleSizeChange(size)}
      className={selectedSize === size ? 'selected' : ''}
    >
      {size}
    </button>
  ));

  return(
    <div className="product-detail">
      <div className="product-images">
      <Swiper
          spaceBetween={10}
          navigation={true}
          thumbs={{ swiper: thumbsSwiper && !thumbsSwiper.destroyed ? thumbsSwiper : null }}
          modules={[Navigation, Thumbs]}
          className="main-swiper"
        >
          {product.images.map((img, index) => (
            <SwiperSlide key={index}>
              <img src={`${process.env.PUBLIC_URL}/${img}`} alt={`Product ${index + 1}`} />
            </SwiperSlide>
          ))}
        </Swiper>
        <Swiper
          onSwiper={setThumbsSwiper}
          spaceBetween={10}
          slidesPerView={4}
          freeMode={true}
          watchSlidesProgress={true}
          modules={[Navigation, Thumbs]}
          className="thumbs-swiper"
        >
          {product.images.map((img, index) => (
            <SwiperSlide key={index}>
              <img src={`${process.env.PUBLIC_URL}/${img}`} alt={`Thumbnail ${index + 1}`} />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
      <div className="product-info">
        <h1>{product.name}</h1>
        <div className="price">          
          <span className="original-price">{product.price.toLocaleString()}원</span>
          <span className="discount-price">{product.discountPrice.toLocaleString()}원</span>
        </div>
        <div className="color-selection">
          <h3>COLOR</h3>
          {renderColorButtons}
        </div>
        <div className="size-selection">
          <h3>SIZE</h3>
          {renderSizeButtons}
        </div>
        <div className="quantity-selection">
          <h3>수량</h3>
          <button onClick={()=>handleQuantityChange(-1)}> - </button>
          <span>{quantity}</span>
          <button onClick={()=>handleQuantityChange(1)}> + </button>
        </div>
        <div className="total-price">
          <h3>TOTAL</h3>
          <span>{totalPrice.toLocaleString()}원</span>
        </div>
        {error && <div className="error-massage">{error}</div>}
        <div className="action-buttons">
          <button className="buy-now">즉시 구매</button>
          <button className="add-to-cart" onClick={handleAddCart}>장바구니담기</button>
          <button className="wishlist">위시리스트</button>
        </div>
      </div>
    </div>
  )
}

export default ProductDetail;
