import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';


const Container = styled.div`
  text-align:center;
`
const Button = styled.button`
  padding:10px 20px;
  font-size:18px;
  cursor:pointer;
  background-color:#ccc;
  border:none;
  border-radius:7px;
  &:hover{
    background-color:#eee;
  }
`;
const LoadingSpinner = styled.div`
  border:4px solid #eee;
  border-top:4px solid #39b;
  border-radius:50%;
  width:60px;
  height:60px;
  margin:0 auto;
  animation:spin 2.5s linear infinite;
  @keyframes spin{
    0%{ transform:rotate(0deg)}
    100%{ transform:rotate(360deg)}
  }
`;

const cityMapping = {
  '서울':'Seoul',
  '인천':'Incheon',
  '수원':'Suwon-si',
  '성남':'Seongnam-si',
  '고양':'Goyang-si',
  '김포':'Gimpo-si',
  '부산':'Busan',
  '대구':'Daegu',
  '광주':'Gwangju',
  '대전':'Daejeon',
  '울산':'Ulsan',
  '세종':'Sejong'
}

function WeatherPage(){
  const navigate = useNavigate();
  const { region, city } = useParams();
  const [weatherData, setWeatherData] = useState(null);

  useEffect(()=>{
    const fetchWeather = async () =>{
      const apiKey = '28e07dfa61cced0cd42f9dc297ccc2f8';
      const mappedCity = cityMapping[city] || city;
      try{
        const weatherResponse = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
          params:{
            q:`${mappedCity},KR`,
            appid:apiKey,
            units:'metric',
            lang:'kr',
          }
        });
        const forecastResponse = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
          params:{
            q:`${mappedCity},KR`,
            appid:apiKey,
            units:'metric',
            lang:'kr',
          }
        });
        setWeatherData(weatherResponse.data);
        setWeatherData(forecastResponse.data);
      } catch (error) {
        alert(error.response.data.message);
      }
    }
    fetchWeather();
  }, [city])

  const backMove =()=>{
    navigate('/');
  }
  return(
    <Container>
      <h1>{`${region} - ${city}시의 날씨 정보`}</h1>
      {weatherData ? ( //weatherData가 있다면 앞()을 실행하고, 없다면 뒤()를 실행함
        <div>
          <h2>{weatherData.weather[0].description}</h2>
          <p>온도 : {weatherData.main.temp}&deg;C</p>
          <p>체감온도 : {weatherData.main.feels_like}&deg;C</p>
          <p>풍속 : {weatherData.wind.speed} m/s</p>
          <p>습도 : {weatherData.main.humidity}</p>
        </div>
      ) : (
        <div>
          <LoadingSpinner />
          <p>날씨 정보를 불러오는 중입니다...</p>
        </div>
      )}0
      <Button onClick={backMove}>다른지역 선택</Button>
    </Container>
  )
}

export default WeatherPage;
