import React, {useState, useEffect} from 'react';
import './App.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

function App() {
  const [todoInput, setTodoInput]=useState('');
  const [todos, setTodos]=useState([]);
  const [filter, setFilter]=useState('all');

  const getTodos=()=>{
    const saveTodos=localStorage.getItem('todos');
    if(saveTodos){
      setTodos(JSON.parse(saveTodos))
    }
  }
  useEffect(()=>{getTodos();},[]);

  const saveLocalTodos=(todo)=>{
    const newTodos=[...todos,{text:todo, completed:false}];
    setTodos(newTodos);
    localStorage.setItem('todos',JSON.stringify(newTodos))
  }

  const removeLocalTodos=(todoRemove)=>{
    const newTodos=todos.filter(todo=>todo.text!==todoRemove.text); //todo라는 배열을 글자와 지정한 글자만
    setTodos(newTodos);
    localStorage.setItem('todos',JSON.stringify(newTodos));
  }

  const addTodo=(e)=>{
    e.preventDefault();
    if(todoInput.trim()==='') return;
    saveLocalTodos(todoInput);
    setTodoInput('');
  }

  const deleteTodo=(todotToDelete)=>{
    removeLocalTodos(todotToDelete);
  }

  const filterTodos=()=>{
    switch(filter){
      case 'completed':
        return todos.filter(todo=>todo.completed);
      case 'uncompleted':
        return todos.filter(todo=>!todo.completed); //! = false
      default:
        return todos;
    }
  }

  return (
    <div className="App">
      <h1> Todo List </h1>
      <form onSubmit={addTodo}>
        <input type="text"
                className="todo-input"
                value={todoInput}
                onChange={(e)=>setTodoInput(e.target.value)}
                placeholder="할 일을 입력하세요"
        />
        <button type="submit" className="todo-button">
          <i class="fas fa-plus-square"></i>
        </button>
      </form>
      <div className="filter-todo">
        <select onChange={(e)=>setFilter(e.target.value)}>
          <option value="all">전체보기</option>
          <option value="completed" class="complete">완료 보기</option>
          <option value="uncompleted">미실행 보기</option>
        </select>
      </div>
      <div class="todo-list">
        {
          filterTodos().map((todo, index)=>( //for문 대신에 사용 가능한 map함수
            <div className="todo" key={index}>
              <p className={`todo-item ${todo.completed ? 'completed':''}`}>
                {todo.text}
              </p>
              <button
                onClick={()=> deleteTodo(todo)}
                className="trash-btn"
              >
                <i className="fas fa-trash"></i>
              </button>
              <button
                onClick={()=>{
                  const updatedTodos=todos.map(t=>
                    t.text===todo.text? {...t,completed:!t.completed}:t
                  );
                  setTodos(updatedTodos);
                  localStorage.setItem('todos',JSON.stringify(updatedTodos));
                }}
                className="complete-btn"              
              >
                <i className="fas fa-check"></i>
              </button>
            </div>
          ))
        }
      </div>
    </div>
  );
}

export default App;
