import React,{ useState } from 'react';

function CitySelector({citiesRegion, selectedRegion, setSelectedRegion, setSelectedCity, onCitySelect}){

  const [selecteCity, setSelectCityState] = useState('');
  const regionChange = (e) => {
    setSelectedRegion(e.target.value);
    setSelectedCity(''); // city가 사라짐
    setSelectCityState('');    
  }
  const cityChange = (e) => {
    const city = e.target.value;
    setSelectCityState(city);
    onCitySelect(city);
  }

  return(
    <div>
      <select value={selectedRegion} onChange={regionChange}>
        <option value="">지역 선택</option>
        {
          Object.keys(citiesRegion).map((region)=>(
            <option key={region} value={region}>{region}</option>
          ))
        }
      </select>
      {selectedRegion && (
      <select value={selecteCity} onChange={cityChange}>
        <option value="">도시 선택</option>
        {
          citiesRegion[selectedRegion].map((city)=>(
            <option key={city} value={city}>{city}</option>
          ))
        }
      </select>
      )}
    </div>
  )
}

export default CitySelector;
