import { legacy_createStore as createStore } from 'redux';

const initState = 10;

const reducer = ( state = initState, action )=>{
  if( action.type==='INCREMENT' ){
    return state+1;
  }else if( action.type==='DECREMENT' ){
    return state-1;
  }else{
    return state;
  }
}
const store = createStore(reducer);

export default store;
