import React, { useState, useEffect } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Routes, Route, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import { Navbar, Nav, NavDropdown, Container, Row, Col, Button} from 'react-bootstrap';
import Company from './pages/Company';
import Business from './pages/Business';
import Esg from './pages/Esg';
import Detail from './pages/Detail';
import Cart from './pages/Cart';
/* import logoImg from './logo.jpg'; */
const TopBox = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom:20px;
  margin-top:30px;
`;


function App() {
  let navigate = useNavigate();
  let [newPdt, setNewPdt] = useState([]);
  let [once, setOnce] = useState(false);
  let [cartItems, setCartItems] = useState([]);

  const addToCart = (item)=>{
    setCartItems([...cartItems, item]);
  }
  const removeFromCart = (id)=>{
    setCartItems( cartItems.filter(item=> item.id!== id))
  }

  useEffect(()=>{
    const fetchData = async () =>{
      try{
        const response = await axios.get('/data.json');
        setNewPdt(response.data);
      } catch(error){
        console.error('Error fetching data : ', error)
      }
    };
    fetchData();
  }, []);

  return (
    <div className="App">
      <Navbar expand="lg">        
        <Navbar.Brand onClick={() => navigate('/')} className="logo">
          {/*<img src={logoImg} /> */}
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav"/>
        <Navbar.Collapse id="navbar-nav">
          <Nav>
            <Nav.Link onClick={() => navigate('/')}>Home</Nav.Link>
            <NavDropdown title="Company" id="nav-dropdown">
              <NavDropdown.Item onClick={()=>navigate('/company')}>INTRODUCTION</NavDropdown.Item>
              <NavDropdown.Item onClick={()=>navigate('/company/business')}>BUSSINESS AREA</NavDropdown.Item>
              <NavDropdown.Item onClick={()=>navigate('/company/esg')}>ESG</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link onClick={() => navigate('/cart')}>Cart</Nav.Link>
          </Nav>
        </Navbar.Collapse>        
      </Navbar>

      <Routes>
        <Route path="/" element={<Main newPdt={newPdt} setNewPdt={setNewPdt} once={once} setOnce={setOnce} />} />
        <Route path="/company" element={<Company />}>
          <Route path="business" element={<Business />} />
          <Route path="esg" element={<Esg />} />
        </Route>
        <Route path="/detail/:id" element={<Detail newPdt={newPdt} addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} removeFromCart={removeFromCart} />} />
      </Routes>

    </div>
  );
}
function Main({newPdt, setNewPdt, once, setOnce}){
  return(
    <section>
      <div className="main-visual">
        <img src="/img/1672995210793.jpg" width="100%" alt="" />
      </div>
      <Container>
        <TopBox>
          <h3> 이번 주 신상품</h3>
          <Button onClick={()=>{
            if(!once){
              axios.get('/newData.json')
                .then((result)=>{
                  let copyData = [...newPdt, ...result.data]
                  setNewPdt(copyData);
                  setOnce(true);
                })
                .catch((error)=>{
                  console.error('Error more products:',error)
                })
            }
          }}>더보기</Button>
        </TopBox>
        <Row>
          {
            newPdt.map((item, idx)=>(
              <Colmd contents={item} key={idx} />
            ))
          }
        </Row>
      </Container>
    </section>
  )
}
function Colmd({contents}){
  let navigate = useNavigate();
  return(
    <Col md={3}>
      <img src={`/img/${contents.src}`} width="90%" alt="" 
            onClick={()=> navigate(`/detail/${contents.id}`)}/>
      <h4>{contents.title}</h4>
      <p>{contents.text}</p>
      <p>{contents.price}</p>
    </Col>
  )
}

export default App;
