import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  text-align: center;
`;

const Button = styled.button`
  text-indent:-9999px;
  border: none;
  cursor: pointer;
  padding: 0;
  position: fixed;
  right: 15px;
  top: 20px;
`;

const LoadingSpinner = styled.div`
  position: absolute;
  top: 50%;
  width: 300px;
  left: 50%;
  margin-left: -150px;
  height: 3px;
  background: rgba(255, 255, 255, .1);
  &:before {
    animation: progress1 4s infinite;
    transform-origin: 0 0;
    content: "";
    display: block;
    width: 300px;
    height: 100%;
    background: linear-gradient(to right, rgba(255, 255, 255, 0.1) 10%, rgba(255, 255, 255, 0.4) 80%, rgba(255, 255, 255, 1));
  }
  &:after {
    content: "";
    position: absolute;
    animation: progress2 4s infinite;
    transform-origin: 90% 50%;
    margin-left: -24px;
    top: -9px;
    width: 30px;
    height: 21px;
    border-radius: 2px;
    background: rgba(210, 189, 255, .55);
    filter: blur(8px);
    box-shadow: 0 0 10px 6px rgba(210, 189, 255, .4),
                -20px 0 15px 4px rgba(210, 189, 255, .3),
                -40px 0 15px 2px rgba(210, 189, 255, .2),
                -60px 0 10px 1px rgba(210, 189, 255, .1),
                -80px 0 10px 1px rgba(210, 189, 255, .05);
  }
  @keyframes progress1 {
    0% {
      transform: scalex(0);
      opacity: .5;
    }
    90% {
      transform: scalex(1);
      opacity: 1;
    }
    100% {
      transform: scalex(1);
      opacity: 0;
    }
  }
  
  @keyframes progress2 {
    0% {
      transform: scale(.3, .8) translatez(0);
      opacity: 0;
    }
    90% {
      transform: scale(1, 1) translatex(300px) translatez(0);
      opacity: 1;
    }
    100% {
      transform: scale(1, 1) translatex(300px) translatez(0);
      opacity: 0;
    }
  }
`;

const cityMapping = {
  '서울': 'Seoul',
  '인천': 'Incheon',
  '수원': 'Suwon-si',
  '성남': 'Seongnam-si',
  '고양': 'Goyang-si',
  '부산': 'Busan',
  '대구': 'Daegu',
  '광주': 'Gwangju',
  '대전': 'Daejeon',
  '울산': 'Ulsan',
  '세종': 'Sejong'
};

function getCustomWeatherDescription(description) {
  switch (description.toLowerCase()) {
    case 'clear sky':
      return '맑은 하늘';
    case 'few clouds':
      return '조금 구름';
    case 'scattered clouds':
      return '흩어진 구름';
    case 'rain':
    case 'light rain':
      return '비 오는 날';
    case 'snow':
      return '눈 오는 날';
    default:
      return description; // 기본적으로 원래의 설명을 사용
  }
}

// 날씨와 시간에 따라 배경 클래스를 반환하는 함수
function getBackgroundClass(weather, hour) {
  if (!weather) return 'default';

  if (weather.toLowerCase() === 'clear sky') {
    return hour >= 6 && hour < 18 ? 'day-clear' : 'night-clear';
  } else if (weather.toLowerCase().includes('rain')) {
    return 'rainy';
  } else if (weather.toLowerCase() === 'snow') {
    return 'snowy';
  } else if (weather.toLowerCase().includes('cloud')) {
    return hour >= 6 && hour < 18 ? 'day-cloudy' : 'night-cloudy';
  } else {
    return 'default';
  }
}

function WeatherPage() {
  const navigate = useNavigate();
  const { city } = useParams();
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [backgroundClass, setBackgroundClass] = useState('default');
  const now = Date.now();
  const twentyFourHoursLater = now + 24 * 60 * 60 * 1000; // 현재 시간 + 24시간

  // 날씨 데이터 로드 후 배경 클래스 업데이트
  useEffect(() => {
    if (weatherData) {
      const weatherDescription = weatherData.weather[0].description;
      const currentHour = new Date().getHours();
      const newBackgroundClass = getBackgroundClass(weatherDescription, currentHour);
      setBackgroundClass(newBackgroundClass);
    }
  }, [weatherData]);

  // 날씨 및 예보 데이터 fetch
  useEffect(() => {
    const fetchWeather = async () => {
      setIsLoading(true);
      const apiKey = '28e07dfa61cced0cd42f9dc297ccc2f8'; // API 키
      const mappedCity = cityMapping[city] || city; // 도시 매핑 (필요에 따라 정의)

      try {
        const weatherResponse = await axios.get(`https://api.openweathermap.org/data/2.5/weather`, {
          params: {
            q: `${mappedCity},KR`,
            appid: apiKey,
            units: 'metric',
            lang: 'kr',
          }
        });
        const forecastResponse = await axios.get(`https://api.openweathermap.org/data/2.5/forecast`, {
          params: {
            q: `${mappedCity},KR`,
            appid: apiKey,
            units: 'metric',
            lang: 'kr',
          }
        });
        setWeatherData(weatherResponse.data);
        setForecastData(forecastResponse.data);
      } catch (error) {
        alert(error.response.data.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchWeather();
  }, [city]);

  // 뒤로 가기 버튼 핸들러
  const backMove = () => {
    navigate('/');
  };

  return (
    <div className={`background ${backgroundClass}`}>
      <div className="weatherpage">
        <h2>{`${city}`}</h2>
        {isLoading ? (
          <div className="loading">
            <p>날씨 정보를 불러오는 중입니다...</p>
          </div>
        ) : (
          weatherData && forecastData && (
            <div>
              <div className="today">
                <div className='currentTemp'>
                  <img
                    src={`https://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`}
                    alt={weatherData.weather[0].description}
                    style={{ width: '120px', height: '120px' }}
                  />
                  <p>{Math.round(weatherData.main.temp)}&deg;</p>
                </div>
                <h3>{getCustomWeatherDescription(weatherData.weather[0].description)}</h3>
                <div className='temp'>
                  <p>최저 : {Math.round(weatherData.main.temp_min)}&deg;</p>
                  <p>최고 : {Math.round(weatherData.main.temp_max)}&deg;</p>
                </div>
              </div>
              <div className="hour">
                <div className="scroll-container">
                  {forecastData.list
                    .filter((forecast) => forecast.dt * 1000 >= now && forecast.dt * 1000 <= twentyFourHoursLater)
                    .map((forecast, index) => (
                      <div key={index} className="forecast">
                        <p><strong>{new Date(forecast.dt * 1000).toLocaleTimeString([], { hour: '2-digit', hour12: false })}</strong></p>
                        <img
                          src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`}
                          alt={forecast.weather[0].description}
                          style={{ width: '50px', height: '50px' }}
                        />
                        <p>{Math.round(forecast.main.temp)}&deg;C</p>
                      </div>
                    ))}
                </div>
              </div>
              <div className="week">
                {forecastData.list.filter((_, index) => index % 8 === 0).map((forecast, index) => {
                  const forecastDate = new Date(forecast.dt * 1000);
                  const isToday = forecastDate.toDateString() === new Date().toDateString();
                  const displayDate = isToday ? '오늘' : forecastDate.toLocaleDateString(undefined, { weekday: 'long' });
                  return (
                    <div key={index} className="forecast">
                      <div className="day">
                        <p><strong>{displayDate}</strong></p>
                        <p>{new Date(forecast.dt * 1000).toLocaleDateString(undefined, { month: '2-digit', day: '2-digit' })}</p>
                      </div>
                      <img
                        src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`}
                        alt={forecast.weather[0].description}
                        style={{ width: '50px', height: '50px' }}
                      />
                      <p>{Math.round(forecast.main.temp_min)}&deg;</p>
                      <p>{Math.round(forecast.main.temp_max)}&deg;</p>
                    </div>
                  );
                })}
              </div>
            </div>
          )
        )}
        <button onClick={backMove} className="more">뒤로</button>
      </div>
    </div>
  );
}

export default WeatherPage;
